package day03java;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//widening casting

byte mango=120;
int imango=mango;
System.out.println("mango rate:"+imango);

// Narrowing casting

int apple=110;
byte iapple=(byte)apple;
System.out.println("apple rate :"+iapple);

int berry = 130;
byte iberry = (byte)berry;
System.out.println("Berry rate : "+iberry);



	}

}
